function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),s=document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active-burger"),s.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);const list=document.querySelector(".list"),items=document.querySelectorAll(".blocks__item");function filter(){list.addEventListener("click",(e=>{const t=e.target.dataset.id,s=e.target;switch(s.classList.contains("list__item")&&(listItems.forEach((e=>e.classList.remove("active"))),s.classList.add("active")),t){case"all":getItems("blocks__item");break;case"winter":case"spring":case"autumn":case"summer":getItems(t)}}))}function getItems(e){items.forEach((t=>{t.classList.contains(e)?t.style.display="block":t.style.display="none"}))}listItems=document.querySelectorAll(".list__item"),filter();